import React, { useState } from "react";
import Modal, { ModalProvider, BaseModalBackground } from "styled-react-modal";

import SignOutButton from "../Auth/SignOut.jsx";
import { withRouter } from "react-router-dom";

import { AuthUserContext } from "../Session";

// import { Box, Button, Heading } from "grommet";

import { Link } from "react-router-dom";

import { SignUpForm } from "../Auth/SignUp.jsx";
import { SignInForm } from "../Auth/SignIn.jsx";

import styled from "styled-components";

//styled modal is css for pop up
const StyledModal = Modal.styled`
  width: 30rem;
  height: 30rem;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: white;
  opacity: ${props => props.opacity};
  transition: opacity ease 1000ms;
  border-radius: 30px;
`;

const RegisterLink = styled(Link)`
  text-decoration: none;
  color: black;
  border: 1px solid gold;
  background-color: gold;
  border-radius: 5px;
  font-size: 1.5rem;
  padding: 5px 20px;
  font-family: "Zilla Slab", serif;
  margin-right: 20px;
  &:hover {
    background-color: yellow;
  }
`;

const Navbar = styled.div`
  position: absolute;
  display: flex;
  justify-content: space-between;
  width: 100%;
  padding-top: 20px;
`;

const LoginLink = styled(Link)`
  text-decoration: none;
  color: white;
  margin-right: 10px;
  font-size: 1.5rem;
  padding: 5px 20px;
  font-family: "Zilla Slab", serif;
  border-radius: 5px;
`;

function SignUpButton() {
  const [isOpen, setIsOpen] = useState(false);
  const [opacity, setOpacity] = useState(0);

  function toggleModal(e) {
    setIsOpen(!isOpen);
  }

  function afterOpen() {
    setTimeout(() => {
      setOpacity(1);
    }, 20);
  }

  function beforeClose() {
    return new Promise(resolve => {
      setOpacity(0);
      setTimeout(resolve, 200);
    });
  }

  return (
    <div>
      <RegisterLink onClick={toggleModal}>Sign Up</RegisterLink>
      <StyledModal
        isOpen={isOpen}
        afterOpen={afterOpen}
        beforeClose={beforeClose}
        onBackgroundClick={toggleModal}
        onEscapeKeydown={toggleModal}
        opacity={opacity}
        backgroundProps={{ opacity }}
      >
        <SignUpForm toggleModal={toggleModal} />

        {/* <button onClick={toggleModal}>Close me</button> */}
      </StyledModal>
    </div>
  );
}

function LoginButton() {
  const [isOpen, setIsOpen] = useState(false);
  const [opacity, setOpacity] = useState(0);

  function toggleModal(e) {
    setIsOpen(!isOpen);
  }

  function afterOpen() {
    setTimeout(() => {
      setOpacity(1);
    }, 20);
  }

  function beforeClose() {
    return new Promise(resolve => {
      setOpacity(0);
      setTimeout(resolve, 200);
    });
  }
  return (
    <div>
      <LoginLink onClick={toggleModal}>Login</LoginLink>
      <StyledModal
        isOpen={isOpen}
        afterOpen={afterOpen}
        beforeClose={beforeClose}
        onBackgroundClick={toggleModal}
        onEscapeKeydown={toggleModal}
        opacity={opacity}
        backgroundProps={{ opacity }}
      >
        <SignInForm toggleModal={toggleModal} />

        {/* <button onClick={toggleModal}>Close me</button> */}
      </StyledModal>
    </div>
  );
}

const Navigation = props => {
  const landingRedirect = () => {
    props.history.push("/");
  };
  return (
    <Navbar>
      <Box direction='row' gap='small'>
        <Heading level='3' margin='none'>
          <i
            class='fas fa-wifi'
            style={{ color: "gold", margin: "0 20px" }}
          ></i>
          <Button
            onClick={landingRedirect}
            label='HiveStack'
            color='white'
            plain='true'
          />
        </Heading>
      </Box>
      <AuthUserContext.Consumer>
        {authUser => (authUser ? <NavigationAuth /> : <NavigationNonAuth />)}
      </AuthUserContext.Consumer>
    </Navbar>
  );
};

const NavigationAuth = () => (
  <Box direction='row' justify='right' gap='small'>
    <SignOutButton />
  </Box>
);

const FadingBackground = styled(BaseModalBackground)`
  opacity: ${props => props.opacity};
  transition: opacity ease 1000ms;
`;

const NavigationNonAuth = () => (
  <Box direction='row' justify='right' gap='small'>
    <ModalProvider backgroundComponent={FadingBackground}>
      <LoginButton />
    </ModalProvider>
    <ModalProvider backgroundComponent={FadingBackground}>
      <SignUpButton />
    </ModalProvider>
  </Box>
);

export default withRouter(Navigation);
